    private void btnaddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnaddActionPerformed

        String Mname = txtName.getText();
        String Mcontact = txtContact.getText();
        String Mtype = (String) cmbMemberType.getSelectedItem();

        Member memberOne = new Member(Mname, Mcontact, Mtype);
        m1.addMember(memberOne);
    }//GEN-LAST:event_btnaddActionPerformed

    private void btnupdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnupdateActionPerformed
        String mtd[] = m1.getDataFromtable(tblMember);

        String name = txtName.getText();
        String conNo = txtContact.getText();
        String type = "";

        if (cmbMemberType.getSelectedItem() == "Admin") {
            type = "Admin";
        } else if (cmbMemberType.getSelectedItem() == "User") {
            type = "User";
        }

        if (name.isEmpty() || conNo.isEmpty() || type.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Please Select, You Need to Update Row!");
        } else {
            Member m = new Member(name, conNo, type);
            m1.updateMemberDetails(m, tblMember, mSearch, mtd);
            m1.textFieldClear(txtName,txtContact);
            m1.showOnTable(tblMember, mSearch);
        }
    }//GEN-LAST:event_btnupdateActionPerformed

    private void btndeleateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btndeleateActionPerformed
            String mtd[] = m1.getDataFromtable(tblMember);
        int x = JOptionPane.showConfirmDialog(null, "Are you want to delete The Member Details");

        if (x == 0) {
            m1.deleteMember(tblMember, mSearch, mtd);
            JOptionPane.showMessageDialog(null, "Member Details Delete successfully.");
            m1.showOnTable(tblMember, mSearch); // Refresh table data if necessary
            m1.textFieldClear(txtName,txtContact);
        }
    }//GEN-LAST:event_btndeleateActionPerformed

