    private void tblMemberMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblMemberMouseClicked
        String mtd[] = m1.getDataFromtable(tblMember);
        txtName.setText(mtd[0]);
        txtContact.setText(mtd[1]);
        if ("Admin".equals(mtd[2])) {
            cmbMemberType.setSelectedItem("Admin");
        } else {
            cmbMemberType.setSelectedItem("User");
        }
    }//GEN-LAST:event_tblMemberMouseClicked

